import {take, put, call, delay, takeLatest, all, fork} from 'redux-saga/effects';
import axios from 'axios';
import {
  LOG_IN_SUCCESS, LOG_OUT_SUCCESS, LOG_IN_REQUEST,
  LOG_IN_FAILURE, LOG_OUT_REQUEST, LOG_OUT_FAILURE, 
  SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE, 
  FOLLOW_REQUEST, FOLLOW_SUCCESS, FOLLOW_FAILURE,
  UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS, UNFOLLOW_FAILURE,
  LOAD_MY_INFO_REQUEST, LOAD_MY_INFO_SUCCESS, LOAD_MY_INFO_FAILURE,
  } from '../reducers/user';

//ÏöîÏ≤≠Ïù¥ Ïã§Ìå®Ìï† Í≤ÉÏùÑ ÎåÄÎπÑÌï¥ÏÑú try, catchÎ°ú Í∞êÏã∏Ï§òÏïº ÌïúÎã§. 
//yield callÌï¥ÏÑú loginAPIÎ•º Ïã§ÌñâÌïúÍ≥† return Í∞íÏùÑ resultÎ°ú Î∞õÎäîÎã§. 
//yield putÏùÄ actionÏùÑ dispatch ÌïòÎäî Í∏∞Îä•ÏùÑ ÌïúÎã§. 

//LogIn
function logInAPI(data) {
  return axios.post('/user/login', data);
}

function* logIn(action) {
  try {
    //ÏÑúÎ≤ÑÍ∞Ä ÏïÑÏßÅ Ïïà ÎßåÎì§Ïñ¥ÏßÑÍ±∞Îã§. 
    const result = yield call(logInAPI, action.data);
    yield put({
      type: LOG_IN_SUCCESS,
      data: result.data,
    });
  } catch (err) {
    console.error("login err", err);
    yield put({
      type: LOG_IN_FAILURE,
      error: err.response.data,
    });
  }
}

//LogOut
function logOutAPI() {
  return axios.post('/user/logout');
}

function* logOut() {
  try {
    yield call(logOutAPI);
    yield put({
      type: LOG_OUT_SUCCESS,
    });
  } catch (err) {
    console.error("saga logout err", err);
    yield put({
      type: LOG_OUT_FAILURE,
      error: err.response.data,
    })
  }
}

//SignUp
function signUpAPI(data) {
  return axios.post('/user', data); //dataÎäî ÏÑúÎ≤ÑÎ°ú req.bodyÎ°ú ÎÑòÏñ¥Í∞ÑÎã§.  
}

function* signUp(action) {
  try {
    const result = yield call(signUpAPI, action.data);
    yield put({
      type: SIGN_UP_SUCCESS,
    });
  } catch (err) {
    console.log("signUp error", err);
    yield put({
      type: SIGN_UP_FAILURE,
      error: err.response.data,
    })
  }
}

//Load my info
function loadUserAPI() {
  return axios.get('/user');  
}

function* loadUser(action) {
  try {
    const result = yield call(loadUserAPI, action.data);
    yield put({
      type: LOAD_MY_INFO_SUCCESS,
      data: result.data,
    });
  } catch (err) {
    yield put({
      type: LOAD_MY_INFO_FAILURE,
      error: err.response.data,
    })
  }
}


//rootSaga ÎßåÎì§Ïñ¥ ÎëêÍ≥† ÏõêÌïòÎäî ÎπÑÎèôÍ∏∞ actionÏùÑ ÌïòÎÇòÏî© ÎÑ£Ïñ¥Ï§ÄÎã§.
//LOG_IN_xxxÏù¥ÎùºÎäî actionÏù¥ Ïã§ÌñâÎê†Îïå ÍπåÏßÄ take = Í∏∞Îã§Î¶¨Í≤†Îã§ Îäî ÏùòÎØ∏Ïù¥Îã§. 
//actionÏù¥ Ïã§ÌñâÎêòÎ©¥ logIn Ìï®ÏàòÎ•º Ïã§ÌñâÌïúÎã§.

//eventListener Í∞ôÏùÄ Ïó≠Ìï†ÏùÑ ÌïúÎã§. 
function* watchLogIn() {
  console.log("watchlogin üòé");
  yield takeLatest(LOG_IN_REQUEST, logIn);
}

function* watchLogOut() {
  yield takeLatest(LOG_OUT_REQUEST, logOut);
}

function* watchSignUP() {
  yield takeLatest(SIGN_UP_REQUEST, signUp);
}

function* watchFollow() {
  yield takeLatest(FOLLOW_REQUEST, follow);
}

function* watchUnfollow() {
  yield takeLatest(UNFOLLOW_SUCCESS, unfollow);
}

function* watchLoadUser() {
  yield takeLatest(LOAD_MY_INFO_REQUEST, loadUser);
}



export default function* userSaga() {
  yield all([
    fork(watchLoadUser),
    /* fork(watchFollow),
    fork(watchUnfollow), */
    fork(watchLogIn),
    fork(watchLogOut),
    fork(watchSignUP),
  ])
}
