export const initialState = {
  logInLoading: false, //로그인 시도중, true면 로딩창을 띄운다. 
  logInDone: false,
  logInError: null, //false로 해도 된다. 
  logOutLoading: false, //로그아웃 시도중, true면 로딩창을 띄운다.
  logOutDone: false,
  logOutError: null,
  signUpLoading: false, //가입하기 시도중
  signUpDone: false,
  signUpError: null,
  me: null, //me는 로그인한 사용자 정보이다. 
  signUpDate: {},
  loginDate: {},
}

//오타에 취약한 애들은 변수로 만들자. 
export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export const FOLLOW_REQUEST = 'FOLLOW_REQUEST';
export const FOLLOW_SUCCESS = 'FOLLOW_SUCCESS';
export const FOLLOW_FAILURE = 'FOLLOW_FAILURE';

export const UNFOLLOW_REQUEST = 'UNFOLLOW_REQUEST';
export const UNFOLLOW_SUCCESS = 'UNFOLLOW_SUCCESS';
export const UNFOLLOW_FAILURE = 'UNFOLLOW_FAILURE';

const dummyUser = (data) => ({
  ...action.data,
  nickname : '제로초',
  id: 1, 
  //시퀄라이즈에서 합쳐주기 때문에 대문자이다. 
  Posts: [],
  Followings: [],
  Followers: [],
})

//동적 action (=action creator)
//SUCCESS와 FAILURE 는 saga가 put으로 호출해 준다. 
export const loginRequestAction = (data) => {
  return {
    type : 'LOG_IN_REQUEST',
    data,
  }
};

export const logoutRequestAction = () => {
  return {
    type : 'LOG_OUT_REQUEST',
  }
};

//case 로그인 요청
//data를 보여줄 것인가는 정책에 따라서 다르다. 
//1. 로딩창이 돌아가고 있을때 배경화면에 데이터를 유지해서 보여줄것인가 
//2. 그 데이터마저 없애서 보여줄것인가 me : null 을 추가해준다.

const reducer = (state = initialState, action) => {
  switch (action.type) {
    //로그인
    case 'LOG_IN_REQUEST' : 
      return {
        ...state,
        logInLoading : true,
        logInError: null, //로딩할때는 error는 없애준다. 
        logInDone: false,
      };
    case 'LOG_IN_SUCCESS' : 
      return {
        ...state,
        logInLoading: false,
        logInDone : true,
        me: dummyUser(action.data), 
      };
    case 'LOG_IN_FAILURE' : 
      return {
        ...state,
        logInLoading : false,
        logInError : action.error,
      };
    //로그아웃
    case 'LOG_OUT_REQUEST' : 
      return {
        ...state,
        logOutLoading : true,
        logOutDone: false, 
        logOutError: null,
      };
    case 'LOG_OUT_SUCCESS' : 
      return {
        ...state,
        logOutLoading : false,
        logOutDone : true,
        me: null,
      };
    case 'LOG_OUT_FAILURE' : 
      return {
        ...state,
        logOutLoading: false,
        logOutError: action.error,
      };

      //사인업
      case 'SIGN_UP_REQUEST' : 
      return {
        ...state,
        signUpLoading : true,
        signUpDone: false, 
        signUpError: null,
      };
    case 'SIGN_UP_SUCCESS' : 
      return {
        ...state,
        signUpLoading : false,
        signUpDone : true,
      };
    case 'SIGN_UP_FAILURE' : 
      return {
        ...state,
        signUpLoading: false,
        signUpError: action.error,
      };
    default:
      return state;
  }
}

export default reducer;
