import shortId from 'shortid';

//대문자로 시작하는 애들은 서버에서 주는 데이터이다. 
//id가 다 붙어있어야 한다. 
export const initialState = {
  mainPosts: [{
    id: 1,
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 번째 게시글 #해시태그 #익스프레스',
    Images: [{
      id: shortId.generate(),
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }, {
      id: shortId.generate(),
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }, {
      id: shortId.generate(),
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }],
    Comments: [{
      id: shortId.generate(),
      User: {
        nickname: 'nero',
      },
        content: '스페인 가고 싶다.',
    }, {
      id: shortId.generate(),
      User: {
        nickname: 'hero',
      },
      content: '나도 가고 싶다.'
    }]
  }],
  imagePaths: [],
  addPostLoading: false,
  addPostDone: false,
  addPostError: null,
  removePostLoading: false,
  removePostDone: false,
  removePostError: null,
  addCommentLoading: false,
  addCommentDone: false,
  addCommentError: null,
}

//action
 //이렇게 변수로 빼주면 재활용할 수 있고 오타가 났을때 재활용 할 수 있다. 
export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const REMOVE_POST_REQUEST = 'ADD_POST_REQUEST';
export const REMOVE_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const REMOVE_POST_FAILURE = 'ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';

//동적 action creator
export const addPost = (data) => ({
  type: ADD_POST_REQUEST,
  data, //쓴 게시글을 받아서 보내준다. 
});

export const addComment = (data) => ({
  type: ADD_COMMENT_REQUEST,
  data, //쓴 글을 받아서 보내준다. 
});

const dummyPost = (data) => ({
  id : data.id,
  content: data.content,
  User: {
    id: 1, 
    nickname: '제로초',
  },
  Images: [],
  Comments: [],
});

const dummyComment = (data) => ({
  id : shortId.generate(),
  content: data,
  User: {
    id: 1, 
    nickname: '제로초',
  },
})

const reducer = (state = initialState, action) => {
  switch (action.type) {
    //addPost
    case ADD_POST_REQUEST :
      return {
        ...state, 
        addPostLoading: true,
        addPostDone: false,
        addPostError: null, //null 아니라 false 된다. 
      };
    case ADD_POST_SUCCESS : 
      return {
        ...state,
        mainPosts: [dummyPost(action.data), ...state.mainPosts], //mainPost가 앞에 들어가야 한다. 그래야 새로운 글이 위에 올라간다. 
        addPostLoading: false,
        addPostDone: true,
      };
    case ADD_POST_FAILURE : 
      return {
        ...state, 
        addPostLoading: false,
        addPostError: action.error,
      };

    //addComment
    case ADD_COMMENT_REQUEST : //먼저 게시글의 id를 찾아서 게시글에 접근해서 -> 그 안에 Comments에 접근해서 정보를 넣어준다. 
      return {
        ...state, 
        addCommentLoading: true,
        addCommentDone: false,
        addCommentError: null, //null 아니라 false도 된다. 
      };
      case ADD_COMMENT_SUCCESS : {
        //불변성의 유지하기 위한 코드이다. 
        //원래 있던 comment들은 유지가 되고 새로운 comment가 추가 된다. 
        const postIndex = state.mainPosts.findIndex((v) => v.id === action.data.postId);
        const post = {...state.mainPosts[postIndex]};
        post.Comments = [dummyComment(action.data.content), ...post.Comments]; //mainPost가 앞에 들어가야 한다. 그래야 새로운 글이 위에 올라간다. 
        const mainPosts = [...state.mainPosts];
        mainPosts[postIndex] = post;

        console.log('리듀서에서 커멘트 추가 액션 성공 확인',post.Comments)
        return {
          ...state,
          mainPosts,
          addCommentLoading: false,
          addCommentDone: true,
        };
      }
    case ADD_COMMENT_FAILURE : 
      return {
        ...state, 
        addCommentLoading: false,
        addCommentError: action.error,
      };
    default:
      return state;
  }
}

export default reducer;
