import {take, put, call, delay, takeLatest, all, fork} from 'redux-saga/effects'
import shortId from 'shortid';
import axios from 'axios';
import {
  ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE,
  ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, 
  REMOVE_POST_REQUEST,REMOVE_POST_SUCCESS,REMOVE_POST_FAILURE,
  LOAD_POST_REQUEST, LOAD_POST_SUCCESS,  LOAD_POST_FAILURE,
  } from '../reducers/post';
import { ADD_POST_TO_ME, REMOVE_POST_OF_ME } from '../reducers/user';

//addPost
function addPostAPI(data) {
  return axios.post('/api/post', data);
}

function* addPost(action) {
  try {
    //const result = yield call(addPostAPI, action.data)
    yield delay(1000);
    const id = shortId.generate();
    yield put({
      type: ADD_POST_SUCCESS,
      data: {
        id, //id: id 와 같은 표현이다. 
        content : action.data, //addPost로 받은 게시글 data를 리듀서의 'ADD_POST_SUCCESS'로 넘겨준다. 
      },
    });
    yield put({
      type: ADD_POST_TO_ME,
      data: id, 
    });
  } catch (err) {
    console.log("에러확인",err)
    yield put({
      type: ADD_POST_FAILURE,
      data: err.response.data,
    })
  }
}

//removePost
function removePostAPI(data) {
  return axios.delete('/api/post', data);
}

function* removePost(action) {
  try {
    //const result = yield call(removePostAPI, action.data)
    yield delay(1000);
    //post reducer 조작 부분
    yield put({
      type: REMOVE_POST_SUCCESS,
      data: action.data, //게시글의 id가 담겨 있다. 
    });
    //user reducer 조작 부분
    yield put({
      type: REMOVE_POST_OF_ME,
      data: action.data,
    });
  } catch (err) {
    yield put({
      type: REMOVE_POST_FAILURE,
      data: err.response.data,
    })
  }
}

//AddComment
function addCommentAPI(data) {
  return axios.post(`/api/post/${data.postId}/comment`, data);
}

function* addComment(action) {
  console.log("addComment_saga_확인");
  try {
    //const result = yield call(addPostAPI, action.data)
    yield delay(1000);
    yield put({
      type: ADD_COMMENT_SUCCESS,
      data: action.data,
    });
  } catch (err) {
    console.log("addComment error", err);
    yield put({
      type: ADD_COMMENT_FAILURE,
      data: err.response.data,
    })
  }
}

//loadPosts
function loadPostsAPI(data) {
  return axios.post(`/api/post/${data.postId}/comment`, data);
}

function* loadPosts(action) {
  try {
    //const result = yield call(addPostAPI, action.data)
    yield delay(1000);
    yield put({
      type: LOAD_POST_SUCCESS,
      data: action.data,
    });
  } catch (err) {
    yield put({
      type: LOAD_POST_FAILURE,
      data: err.response.data,
    })
  }
}



//rootSaga 만들어 두고 원하는 비동기 action을 하나씩 넣어준다.
//ADD_xxx_xxx이라는 action이 실행될때 까지 take = 기다리겠다 는 의미이다. 
//action이 실행되면 addPost 함수를 실행한다.

function* watchLoadPosts() {
  yield takeLatest(LOAD_POST_REQUEST, addPost);
}

function* watchAddPost() {
  yield takeLatest(ADD_POST_REQUEST, addPost);
}

function* watchRemovePost() {
  yield takeLatest(REMOVE_POST_REQUEST, removePost)
}

function* watchAddComment() {
  yield takeLatest(ADD_COMMENT_REQUEST, addComment);
}


export default function* postSaga() {
  yield all([
    fork(watchAddPost),
    fork(watchLoadPosts),
    fork(watchRemovePost), 
    fork(watchAddComment),
  ])
}
