import React, {useState, useCallback} from 'react';
import PropTypes from 'prop-types';
import {useSelector} from 'react-redux';
import { Card, Popover, Button } from 'antd';
import { EllipsisOutlined, RetweetOutlined, HeartOutlined, MessageOutlined} from '@ant-design/icons';
import Avatar from 'antd/lib/avatar/avatar';

import PostImages from './PostImages';


const PostCard = ({ post }) => {
  const [liked, setLiked] = useState(false);
  const [commentFormOpened, setcommentFormOpened] = useState(false);

  const onToggleLike = useCallback(() => {
    setLiked((prev) => !prev);
  }, []);
  const onToggleComment = useCallback(() => {
    setcommentFormOpened((prev) => !prev);
  }, []);

  const id = useSelector((state) => state.user?.id);

  return (
    <div>
      <Card 
        cover= {post.Images[0] && <PostImages images = {post.Images} />}
        actions = {[
          <RetweetOutlined key = "retweet"/>,
          liked
            ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onToggleLike} />
            : <HeartOutlined key = "heart" onClick={onToggleComment} />,
          <MessageOutlined key = "comment"/>,
          <Popover key = "more" content={(
            //내가 쓴글이면 수정, 삭제 가능하게 하고 남이 쓴글이면 신고가능하게 하기.
            <Button.Group>
              {id && post.User.id === id  
              ? (
                <>
                  <Button>수정</Button>
                  <Button type="danger">삭제</Button>
                </>
                ) : <Button>신고</Button> }
            </Button.Group>
          )}>
            <EllipsisOutlined />
          </Popover> 
        ]}
        >
          <Card.Meta
            avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
            title={post.User.nickname}
            description={post.content}
          />
      </Card>
      {/* <CommentForm /> */}
      {/* <Comments /> */}
    </div>
  )
}

//initialState 모양대로 proptypes를 정해주면 된다. 
PostCard.propTypes = {
  post: PropTypes.shape({
    id: PropTypes.number,
    User: PropTypes.object,
    content: PropTypes.string,
    createdAt: PropTypes.object,
    Comments: PropTypes.arrayOf(PropTypes.object),
    Images: PropTypes.arrayOf(PropTypes.object),
  }).isRequired,
}
export default PostCard
