import {take, put, delay, takeLatest, all, fork} from 'redux-saga/effects'
import shortId from 'shortid';
import axios from 'axios';
import {
  ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE,
  ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, 
  REMOVE_POST_REQUEST,REMOVE_POST_SUCCESS,REMOVE_POST_FAILURE,
  LOAD_POSTS_REQUEST, LOAD_POSTS_SUCCESS,  LOAD_POSTS_FAILURE, generateDummyPost,
  } from '../reducers/post';
import { ADD_POST_TO_ME, REMOVE_POST_OF_ME, FOLLOW_REQUEST,FOLLOW_SUCCESS ,FOLLOW_FAILURE, UNFOLLOW_REQUEST, UNFOLLOW_SUCCESS ,UNFOLLOW_FAILURE } from '../reducers/user';
import { throttle } from 'redux-saga/effects';

//addPost
function addPostAPI(data) {
  return axios.post('/api/post', data);
}

function* addPost(action) {
  try {
    //const result = yield call(addPostAPI, action.data)
    yield delay(1000);
    const id = shortId.generate();
    yield put({
      type: ADD_POST_SUCCESS,
      data: {
        id, //id: id ÏôÄ Í∞ôÏùÄ ÌëúÌòÑÏù¥Îã§. 
        content : action.data, //addPostÎ°ú Î∞õÏùÄ Í≤åÏãúÍ∏Ä dataÎ•º Î¶¨ÎìÄÏÑúÏùò 'ADD_POST_SUCCESS'Î°ú ÎÑòÍ≤®Ï§ÄÎã§. 
      },
    });
    yield put({
      type: ADD_POST_TO_ME,
      data: id, 
    });
  } catch (err) {
    console.log("ÏóêÎü¨ÌôïÏù∏",err)
    yield put({
      type: ADD_POST_FAILURE,
      data: err.response.data,
    })
  }
}

//removePost
function removePostAPI(data) {
  return axios.delete('/api/post', data);
}

function* removePost(action) {
  try {
    //const result = yield call(removePostAPI, action.data)
    yield delay(1000);
    //post reducer Ï°∞Ïûë Î∂ÄÎ∂Ñ
    yield put({
      type: REMOVE_POST_SUCCESS,
      data: action.data, //Í≤åÏãúÍ∏ÄÏùò idÍ∞Ä Îã¥Í≤® ÏûàÎã§. 
    });
    //user reducer Ï°∞Ïûë Î∂ÄÎ∂Ñ
    yield put({
      type: REMOVE_POST_OF_ME,
      data: action.data,
    });
  } catch (err) {
