export const initialState = {
  mainPosts: [{
    id: 1,
    User: {
      id: 1,
      nickname: '제로초',
    },
    content: '첫 번째 게시글 #해시태그 #익스프레스',
    Image: [{
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }, {
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }, {
      src: 'https://www.theglobetrottingteacher.com/wp-content/uploads/2019/09/Cordoba-Spain-Roman-Bridge.jpg',
    }],
    Comments: [{
      User: {
        nickname: 'nero',
      },
        content: '스페인 가고 싶다.',
    }, {
      User: {
        nickname: 'hero',
      },
      content: '나도 가고 싶다.'
    }]
  }],
  imagePaths: [],
  postAdded: false,
}
//action
const ADD_POST = 'ADD_POST'; //이렇게 변수로 빼주면 재활용할 수 있고 오타가 났을때 재활요 할 수 있다. 
export const addPost = {
  type: ADD_POST,
}

const dummyPost = {
  id : 2,
  content: '더미데이터입니다.',
  User: {
    id: 1, 
    nickname: '제로초',
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST : 
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts], //mainPost가 앞에 들어가야 한다. 그래야 새로운 글이 위에 올라간다. 
        postAdded: true,
      }
    default:
      return state;
  }
}

export default reducer;
