const initialState = {
  name: 'zerocho',
  age: '27',
  password: 'babo'
}

//action
const changeNickName = () => {
  return {
    type : 'CHANGE_NICKNAME',
    data : 'boogicho'
  }
}

//reducer
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CHANGE_NICKNAME' : 
      return {
        ...state,
        name: action.data,
      }
  }
}

export default rootReducer;
