import React, { useState, useCallback, useMemo } from 'react';
import { Form, Input, Button } from 'antd';
import Link from 'next/link';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';

import useInput from '../hooks/useInput';
import { loginAction } from '../reducers';

//ButtonWrapper라는 스타일이 적용된 component를 만들어 사용할 수 있다. 
//ButtonWrapper는 div 태그가 된다. 
const ButtonWrapper = styled.div`
  marginTop: 10px;
`;
const FormWrapper = styled(Form)`
  padding: 10px;
`
//setIsLoggedIn은 더미데이터로 AppLayout에서 넘겨 받음
const LoginForm = () => {
  const dispatch = useDispatch()
  const [id, onChangeId] = useInput('');
  const [password, onChangePassword] = useInput('');

  //로그인을 하는 순간 setIsLoggedIn(true)로 인해 
  //isLoggedIn 이 true로 바뀌고 -> <UserProfile /> 로 바뀐다. 
  //서버가 없어도 useState 이용해서 더미데이터로 로그인을 시켜줄 수 있다. 
  const onSubmitForm = useCallback(() => {
    console.log(id, password);
    dispatch(loginAction({id, password}))
  }, [id, password]);

  return(
    <FormWrapper onFinish={onSubmitForm}>
      <div>
        <label htmlFor = "user-id">아이디</label>
        <br />
        <Input 
          name="user-id" 
          value={id} 
          onChange={onChangeId} 
          required 
        />
      </div>
      <div>
        <label htmlFor="user-password">비밀번호</label>
        <br />
        <Input 
          name="user-id" 
          value={password} 
          onChange={onChangePassword} 
          required 
        />
      </div>
      <ButtonWrapper>
        <Button type="primary" htmlType="submit" laoding={false}>로그인</Button>
        <Link href="/signup"><a><Button>회원가입</Button></a></Link>
      </ButtonWrapper>
    </FormWrapper>
  )
}

export default LoginForm;
