//초기 값들을 initialState에 잡아 둬야 한다. 
const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpDate: {},
    loginDate: {},
  },
  post: {
    mainPosts: [],
  }
}

//동적 action (=action creator)
export const loginAction = (data) => {
  return {
    type : 'LOG_IN',
    data,
  }
};

export const logoutAction = () => {
  return {
    type : 'LOG_OUT',
  }
};

//reducer
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN' : 
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn : true,
          user: action.data,
        },
      };
    case 'LOG_OUT' : 
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn : false,
          user:null,
        },
      };
  }
}

export default rootReducer;
