export const initialState = {
  isLoggedIn: false,
  user: null,
  signUpDate: {},
  loginDate: {},
}

//동적 action (=action creator)
export const loginAction = (data) => {
  return {
    type : 'LOG_IN',
    data,
  }
};

export const logoutAction = () => {
  return {
    type : 'LOG_OUT',
  }
};


const reducer = (state = initialState, action) => {
  switch (action, type) {
    case HYDRATE :  
      console.log('HYDRATE : ', action);
      return {...state, ...action.payload} ;
    case 'LOG_IN' : 
      return {
        ...state,
        isLoggedIn : true,
        user: action.data,
        };
    case 'LOG_OUT' : 
      return {
          ...state,
          isLoggedIn : false,
          user:null,
        };
    default:
      return state;
  }
}

export default reducer;
